// @flow
import React, { Component } from 'react'
import { DragSource as dragSource } from 'react-dnd'
import { connect } from 'react-redux'
import classNames from 'classnames'

import { shouldPureComponentUpdate } from '../../../helper/shouldComponentUpdate'
import * as hoverActions from '../../../actions/cell/drag'
import * as insertActions from '../../../actions/cell/insert'
import { source, collect } from './helper/dnd'

const icon = 'data:image/png;base64,'

class Draggable extends Component {
  componentDidMount() {
    const img = new Image()
    img.onload = () => this.props.connectDragPreview(img)
    img.src = icon
  }

  shouldComponentUpdate = shouldPureComponentUpdate

  props: {
    isLeaf: boolean,
    isOver: boolean,
    isOverCurrent: boolean,
    isDragging: boolean,
    isInsertMode: boolean,
    isLayoutMode: boolean,
    node: { hover: string, inline: string },
    children: any,
    className: string,
    connectDragSource<T>(e: T): T,
    connectDragPreview(image: Image): any
  }

  render() {
    const {
      isLeaf, connectDragSource, isDragging, isLayoutMode, isInsertMode, node: { inline }, children
    } = this.props

    if (!(isLayoutMode || isInsertMode)) {
      return (
        <div className="ory-cell-draggable-container">
          {/* these divs are here to prevent page jumping, they are a placeholder for draggable / draggable-overlay */}
          <div className="ory-cell-draggable-overlay-placeholder" />
          {children}
        </div>
      )
    }

    return connectDragSource(
      <div className={classNames('ory-cell-draggable', { 'ory-cell-draggable-is-dragging': isDragging })}>
        <div className={classNames('ory-cell-draggable-overlay', { [`ory-cell-draggable-inline-${inline}`]: inline, 'ory-cell-draggable-leaf': isLeaf })} />
        {children}
      </div>
    )
  }
}

const mapDispatchToProps = {
  ...hoverActions,
  ...insertActions
}

export default connect(null, mapDispatchToProps)(dragSource(({ dragType }: { dragType: string }) => dragType, source, collect)(Draggable))
